//     <div id="header" >
//       <Canvas concurrent shadowMap camera={{ position: [0, 0, 3], fov: 70 }}>
//         {/* <color attach="background" args={["#fff"]} /> */}
//         <ambientLight intensity={0.2} />

//           <pointLight position={[0, 10, 20]} intensity={2}  />
//           {/* <OrbitControls /> */}
//           <Physics>
//             <Scene />
//             {/* <Scene titlePosi={titlePosi} /> */}
//           </Physics>

//       </Canvas>
//     </div>
//   );
// }

export default Scene;
