import { useLayoutEffect } from 'react';
import { useThree } from 'react-three-fiber';

function Preload({
  all,
  scene,
  camera
}) {
  const {
    gl,
    scene: dScene,
    camera: dCamera
  } = useThree(); // Layout effect because it must run before React commits

  useLayoutEffect(() => {
    const invisible = [];

    if (all) {
      (scene || dScene).traverse(object => {
        if (object.visible === false) {
          invisible.push(object);
          object.visible = true;
        }
      });
    } // Now compile the scene


    gl.compile(scene || dScene, camera || dCamera); // Flips these objects back

    invisible.forEach(object => object.visible = false);
  }, []);
  return null;
}

export { Preload };
